import React, { useState } from 'react';
import { createAccount } from '../utils/api';

function CreateAccountForm() {
  const [formData, setFormData] = useState({
    accountNumber: '',
    accountHolderName: '',
    balance: '',
    accountType: 'Savings',
    creationDate: new Date().toISOString().split('T')[0], // today YYYY-MM-DD
    recipientAccount: '', // optional
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await createAccount({
        accountNumber: formData.accountNumber,
        accountHolderName: formData.accountHolderName,
        balance: parseFloat(formData.balance),
        accountType: formData.accountType,
        creationDate: formData.creationDate,          // send date
        recipientAccount: formData.recipientAccount,  // send recipient account if any
      });
      alert('Account Created! ID: ' + response.data.accountId);
      // reset form (optional)
      setFormData({
        accountNumber: '',
        accountHolderName: '',
        balance: '',
        accountType: 'Savings',
        creationDate: new Date().toISOString().split('T')[0],
        recipientAccount: '',
      });
    } catch (error) {
      alert('Failed to create account: ' + error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="accountNumber"
        placeholder="Account Number"
        value={formData.accountNumber}
        onChange={handleChange}
        required
      />
      <input
        name="accountHolderName"
        placeholder="Account Holder Name"
        value={formData.accountHolderName}
        onChange={handleChange}
        required
      />
      <input
        name="balance"
        placeholder="Balance"
        type="number"
        value={formData.balance}
        onChange={handleChange}
        required
      />
      <select
        name="accountType"
        value={formData.accountType}
        onChange={handleChange}
      >
        <option value="Savings">Savings</option>
        <option value="Current">Current</option>
      </select>

      <label>
        Creation Date: <br />
        <input
          type="date"
          name="creationDate"
          value={formData.creationDate}
          onChange={handleChange}
          required
        />
      </label>

      <input
        name="recipientAccount"
        placeholder="Recipient Account (optional)"
        value={formData.recipientAccount}
        onChange={handleChange}
      />

      <button type="submit">Create Account</button>
    </form>
  );
}

export default CreateAccountForm;
