import React, { useState } from 'react';

function TransferForm({ accountId }) {
  const [date, setDate] = useState('');
  const [recipientAccount, setRecipientAccount] = useState('');
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!date || !recipientAccount || !amount) {
      alert('Please fill in all required fields.');
      return;
    }

    alert(`Transfer Details:
    Type: Transfer
    Date: ${date}
    From Account: ${accountId}
    Recipient Account: ${recipientAccount}
    Amount: $${amount}
    Description: ${description}`);

    // Reset form
    setDate('');
    setRecipientAccount('');
    setAmount('');
    setDescription('');
  };

  return (
    <div>
      <h2>Transfer Money</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Type: <br />
            <input type="text" value="Transfer" readOnly />
          </label>
        </div>

        <div>
          <label>
            Date: <br />
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </label>
        </div>

        <div>
          <label>
            From Account ID: <br />
            <input type="text" value={accountId} readOnly />
          </label>
        </div>

        <div>
          <label>
            Recipient Account: <br />
            <input
              type="text"
              value={recipientAccount}
              onChange={(e) => setRecipientAccount(e.target.value)}
              placeholder="Recipient Account ID"
              required
            />
          </label>
        </div>

        <div>
          <label>
            Amount: <br />
            <input
              type="number"
              min="0.01"
              step="0.01"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="Amount"
              required
            />
          </label>
        </div>

        <div>
          <label>
            Description: <br />
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Optional description"
            />
          </label>
        </div>

        <button type="submit">Transfer</button>
      </form>
    </div>
  );
}

export default TransferForm;
