import React, { useEffect, useState } from 'react';
import { getAccountDetails, getTransactionHistory } from '../api';

function AccountDetails({ accountId }) {
  const [account, setAccount] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        setError(null);

        const accountRes = await getAccountDetails(accountId);
        const transactionsRes = await getTransactionHistory(accountId);

        setAccount(accountRes.data);
        setTransactions(transactionsRes.data);
      } catch (err) {
        setError('Failed to load data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [accountId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!account) return <p>Account not found</p>;

  return (
    <div>
      <h2>Account Details</h2>
      <p><strong>Account Number:</strong> {account.accountNumber}</p>
      <p><strong>Account Holder:</strong> {account.accountHolder}</p>
      <p><strong>Account Type:</strong> {account.accountType}</p>
      <p><strong>Balance:</strong> ${account.balance.toFixed(2)}</p>
      <p><strong>Created Date:</strong> {new Date(account.createdDate).toLocaleDateString()}</p>

      <h3>Transaction History</h3>
      {transactions.length === 0 ? (
        <p>No transactions found.</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0" style={{ width: '100%', marginTop: '1rem' }}>
          <thead>
            <tr>
              <th>Date</th>
              <th>Type</th>
              <th>Amount</th>
              <th>Description</th>
              <th>Recipient Account</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(txn => (
              <tr key={txn.id}>
                <td>{new Date(txn.date).toLocaleString()}</td>
                <td>{txn.type}</td>
                <td>${txn.amount.toFixed(2)}</td>
                <td>{txn.description}</td>
                <td>{txn.recipientAccount || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AccountDetails;
