// export default AccountDashboard;

import React, { useEffect, useState } from 'react';
import { getAllAccounts } from '../utils/api';
import AccountDetails from './AccountDetails'; // assuming you have this

function AccountDashboard() {
  const [accounts, setAccounts] = useState([]);
  const [selectedAccountId, setSelectedAccountId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hoveredId, setHoveredId] = useState(null);

  useEffect(() => {
    getAllAccounts()
      .then(response => {
        setAccounts(response.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load accounts');
        setLoading(false);
      });
  }, []);

  if (loading) return <p style={styles.message}>Loading accounts...</p>;
  if (error) return <p style={styles.message}>{error}</p>;

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Account Dashboard</h1>
      <ul style={styles.list}>
        {accounts.map(acc => (
          <li key={acc.accountId} style={styles.listItem}>
            <button
              onClick={() => setSelectedAccountId(acc.accountId)}
              style={{
                ...styles.button,
                ...(hoveredId === acc.accountId ? styles.buttonHover : {}),
              }}
              onMouseEnter={() => setHoveredId(acc.accountId)}
              onMouseLeave={() => setHoveredId(null)}
            >
              {acc.accountHolderName} - {acc.accountNumber}
            </button>
          </li>
        ))}
      </ul>

      {selectedAccountId && (
        <div style={styles.detailsContainer}>
          <AccountDetails accountId={selectedAccountId} />
        </div>
      )}
    </div>
  );
}

// Inline style object
const styles = {
  container: {
    padding: '30px',
    maxWidth: '700px',
    margin: '50px auto',
    backgroundColor: '#f9fafb', // Light gray background
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
    borderRadius: '12px',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  heading: {
    textAlign: 'center',
    color: '#1e3a8a', // Deep blue
    fontWeight: '700',
    fontSize: '2rem',
    marginBottom: '30px',
    letterSpacing: '1.2px',
  },
  list: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
  },
  listItem: {
    marginBottom: '16px',
  },
  button: {
    backgroundColor: '#3b82f6', // Vibrant blue
    color: '#fff',
    border: 'none',
    padding: '14px 20px',
    fontSize: '18px',
    borderRadius: '10px',
    cursor: 'pointer',
    width: '100%',
    textAlign: 'left',
    boxShadow: '0 2px 6px rgba(59, 130, 246, 0.5)',
    transition: 'background-color 0.25s ease, box-shadow 0.25s ease',
  },
  buttonHover: {
    backgroundColor: '#2563eb', // Darker blue on hover
    boxShadow: '0 4px 12px rgba(37, 99, 235, 0.7)',
  },
  message: {
    textAlign: 'center',
    color: '#6b7280', // Cool gray
    fontSize: '18px',
    fontStyle: 'italic',
  },
  detailsContainer: {
    marginTop: '32px',
    padding: '20px',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
  },
};

export default AccountDashboard;
