import React, { useEffect, useState } from 'react';
import { getAccountDetails } from '../utils/api';

import TransactionHistory from './TransactionHistory';
import { formatCurrency } from '../utils/helpers';

function AccountDetails({ accountId }) {
  const [account, setAccount] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!accountId) return;

    setLoading(true);
    getAccountById(accountId)
      .then(response => {
        setAccount(response.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load account details.');
        setLoading(false);
      });
  }, [accountId]);

  if (!accountId) return <p>Please select an account to view details.</p>;
  if (loading) return <p>Loading account details...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Account Details</h2>
      <p><strong>Account Number:</strong> {account.accountNumber}</p>
      <p><strong>Account Holder:</strong> {account.accountHolderName}</p>
      <p><strong>Account Type:</strong> {account.accountType}</p>
      <p><strong>Balance:</strong> {formatCurrency(account.balance)}</p>
      <p><strong>Created Date:</strong> {new Date(account.createdDate).toLocaleDateString()}</p>

      <TransactionHistory accountId={accountId} />
    </div>
  );
}

export default AccountDetails;
