import React, { useState } from 'react';
import { transferMoney } from '../utils/api';

function TransferForm() {
  const [fromAccountId, setFromAccountId] = useState('');
  const [toAccountId, setToAccountId] = useState('');
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const response = await transferMoney({
        fromAccountId: parseInt(fromAccountId),
        toAccountId: parseInt(toAccountId),
        amount: parseFloat(amount),
        description,
      });
      setMessage(`Transfer successful! New balance: ${response.data.balance}`);
    } catch (error) {
      setMessage(`Transfer failed: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div>
      <h2>Transfer Money</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          placeholder="From Account ID"
          value={fromAccountId}
          onChange={(e) => setFromAccountId(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="To Account ID"
          value={toAccountId}
          onChange={(e) => setToAccountId(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <button type="submit">Transfer</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default TransferForm;
