import React, { useEffect, useState } from 'react';
import { getTransactionHistory } from '../utils/api'; // your API call helper
import { formatCurrency } from '../utils/helpers'; // optional currency formatter

function TransactionHistory({ accountId }) {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!accountId) {
      setTransactions([]);
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

    getTransactionHistory(accountId)
      .then(response => {
        setTransactions(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch transactions.');
        setLoading(false);
      });
  }, [accountId]);

  if (!accountId) return <p>Please select an account to see transactions.</p>;
  if (loading) return <p>Loading transactions...</p>;
  if (error) return <p>{error}</p>;

  if (transactions.length === 0) return <p>No transactions found.</p>;

  return (
    <div>
      <h3>Transaction History</h3>
      <table style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th style={thStyle}>Date</th>
            <th style={thStyle}>Type</th>
            <th style={thStyle}>Amount</th>
            <th style={thStyle}>Description</th>
            <th style={thStyle}>Recipient Account</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(tx => (
            <tr key={tx.id}>
              <td style={tdStyle}>{new Date(tx.date).toLocaleString()}</td>
              <td style={tdStyle}>{tx.type}</td>
              <td style={tdStyle}>{formatCurrency(tx.amount)}</td>
              <td style={tdStyle}>{tx.description || '-'}</td>
              <td style={tdStyle}>{tx.recipientAccount || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

const thStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  backgroundColor: '#f4f4f4',
  textAlign: 'left',
};

const tdStyle = {
  border: '1px solid #ccc',
  padding: '8px',
};

export default TransactionHistory;
