import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useSearchParams } from 'react-router-dom';

import AccountDashboard from './components/AccountDashboard';
import AccountDetails from './components/AccountDetails';
import CreateAccountForm from './components/CreateAccountForm';
import DepositForm from './components/DepositForm';
import WithdrawForm from './components/WithdrawForm';
import TransferForm from './components/TransferForm';
import TransactionHistory from './components/TransactionHistory';

function App() {
  const [selectedAccountId, setSelectedAccountId] = useState(null);

  return (
    <Router>
      <div>
        <nav style={{ padding: '1rem', borderBottom: '1px solid #ccc' }}>
          <Link to="/accounts" style={{ marginRight: '1rem' }}>Accounts</Link>
          <Link to="/create" style={{ marginRight: '1rem' }}>Create Account</Link>
          <Link to="/transactions">Transactions</Link>
        </nav>

        <Routes>
          {/* Redirect root to /accounts */}
          <Route path="/" element={<Navigate to="/accounts" replace />} />

          {/* List accounts */}
          <Route
            path="/accounts"
            element={
              <AccountDashboard
                onSelectAccount={(id) => setSelectedAccountId(id)}
                selectedAccountId={selectedAccountId}
              />
            }
          />

          {/* Account details */}
          <Route
            path="/accounts/:accountId"
            element={<AccountDetailsWrapper />}
          />

          {/* Create a new account */}
          <Route path="/create" element={<CreateAccountForm />} />

          {/* Deposit money */}
          <Route path="/deposit" element={<DepositForm />} />

          {/* Withdraw money */}
          <Route path="/withdraw" element={<WithdrawForm />} />

          {/* Transfer money */}
          <Route path="/transfer" element={<TransferForm />} />

          {/* Transactions page */}
          <Route path="/transactions" element={<TransactionHistoryWrapper />} />

          {/* Fallback route */}
          <Route path="*" element={<h2>404 - Page Not Found</h2>} />
        </Routes>
      </div>
    </Router>
  );
}

// Wrapper to get accountId from URL param for AccountDetails
import { useParams } from 'react-router-dom';

function AccountDetailsWrapper() {
  const { accountId } = useParams();
  return <AccountDetails accountId={parseInt(accountId)} />;
}

// Wrapper to get accountId from URL query parameter for TransactionHistory
function TransactionHistoryWrapper() {
  const [searchParams] = useSearchParams();
  const accountIdParam = searchParams.get('accountId');
  const accountId = accountIdParam ? parseInt(accountIdParam) : null;

  return <TransactionHistory accountId={accountId} />;
}

export default App;
