import React, { useEffect, useState } from 'react';
import { getTransactionHistory } from '../utils/api'; // your axios API helpers

function TransactionHistory({ accountId }) {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!accountId) return;

    setLoading(true);
    getTransactionHistory(accountId)
      .then(response => {
        setTransactions(response.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load transaction history.');
        setLoading(false);
      });
  }, [accountId]);

  if (!accountId) return <p>Please select an account to view transactions.</p>;
  if (loading) return <p>Loading transactions...</p>;
  if (error) return <p>{error}</p>;
  if (transactions.length === 0) return <p>No transactions found.</p>;

  return (
    <div>
      <h2>Transaction History for Account {accountId}</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Description</th>
            <th>Recipient Account</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(tx => (
            <tr key={tx.id}>
              <td>{new Date(tx.date).toLocaleString()}</td>
              <td>{tx.type}</td>
              <td>${tx.amount.toFixed(2)}</td>
              <td>{tx.description || '-'}</td>
              <td>{tx.recipientAccount || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionHistory;
