import React, { useState } from 'react';
import { createAccount } from '../utils/api';

function CreateAccountForm() {
  const [formData, setFormData] = useState({
    accountNumber: '',
    accountHolderName: '',
    balance: '',
    accountType: 'Savings',
    creationDate: new Date().toISOString().split('T')[0], // default to today
    recipientAccount: '', // optional field
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await createAccount({
        accountNumber: formData.accountNumber,
        accountHolderName: formData.accountHolderName,
        balance: parseFloat(formData.balance),
        accountType: formData.accountType,
        creationDate: formData.creationDate,
        recipientAccount: formData.recipientAccount,
      });

      alert('Account Created! ID: ' + response.data.accountId);

      // Reset form after successful submission
      setFormData({
        accountNumber: '',
        accountHolderName: '',
        balance: '',
        accountType: 'Savings',
        creationDate: new Date().toISOString().split('T')[0],
        recipientAccount: '',
      });
    } catch (error) {
      alert('Failed to create account: ' + (error.response?.data?.message || error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>accountNumber</label><br/>
        <input
          name="accountNumber"
          placeholder="Account Number"
          value={formData.accountNumber}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <label>accountHolderName</label>
        <input
          name="accountHolderName"
          placeholder="Account Holder Name"
          value={formData.accountHolderName}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <input
          name="balance"
          placeholder="Balance"
          type="number"
          min="0"
          step="0.01"
          value={formData.balance}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <select
          name="accountType"
          value={formData.accountType}
          onChange={handleChange}
        >
          <option value="Savings">Savings</option>
          <option value="Current">Current</option>
        </select>
      </div>

      <div>
        <label>
          Creation Date: <br />
          <input
            type="date"
            name="creationDate"
            value={formData.creationDate}
            onChange={handleChange}
            required
          />
        </label>
      </div>

      <div>
<label>recipientAccount</label><br/>
        <input
          name="recipientAccount"
          placeholder="Recipient Account (optional)"
          value={formData.recipientAccount}
          onChange={handleChange}
        />
      </div>

      <div>
        <button type="submit">Create Account</button>
      </div>
    </form>
  );
}

export default CreateAccountForm;
