    transition: all 0.3s ease;
    position: relative;
  }

  .nav-link:hover {
    color: #667eea;
    background: rgba(102, 126, 234, 0.1);
    transform: translateY(-1px);
  }

  .nav-link.active {
    color: #667eea;
    background: rgba(102, 126, 234, 0.15);
  }

  .main-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem;
  }

  .page-title {
    font-size: 2rem;
