  }

  .form-input {
    width: 100%;
    padding: 0.75rem 1rem;
    border: 2px solid #e2e8f0;
    border-radius: 8px;
    font-size: 1rem;
    transition: all 0.3s ease;
    background: rgba(255, 255, 255, 0.8);
  }

  .form-input:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
    background: rgba(255, 255, 255, 1);
  }

  .form-select {
    width: 100%;
    padding: 0.75rem 1rem;
    border: 2px solid #e2e8f0;
    border-radius: 8px;
    font-size: 1rem;
    transition: all 0.3s ease;
    background: rgba(255, 255, 255, 0.8);
    cursor: pointer;
  }

  .form-select:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  .btn {
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
  }

  .btn-primary {
    background: linear-gradient(45deg, #667eea, #764ba2);
    color: white;
    box-shadow: 0 4px 15px rgba(102, 126, 234, 0.3);
  }

  .btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(102, 126, 234, 0.4);
  }

  .btn-secondary {
    background: rgba(74, 85, 104, 0.1);
    color: #4a5568;
    border: 2px solid rgba(74, 85, 104, 0.2);
  }

  .btn-secondary:hover {
    background: rgba(74, 85, 104, 0.2);
    transform: translateY(-1px);
  }

  .btn-success {
    background: linear-gradient(45deg, #48bb78, #38a169);
    color: white;
    box-shadow: 0 4px 15px rgba(72, 187, 120, 0.3);
  }

  .btn-success:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(72, 187, 120, 0.4);
  }

  .btn-warning {
    background: linear-gradient(45deg, #ed8936, #dd6b20);
    color: white;
    box-shadow: 0 4px 15px rgba(237, 137, 54, 0.3);
  }

  .btn-warning:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(237, 137, 54, 0.4);
  }

  .btn-danger {
    background: linear-gradient(45deg, #f56565, #e53e3e);
    color: white;
    box-shadow: 0 4px 15px rgba(245, 101, 101, 0.3);
  }

  .btn-danger:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(245, 101, 101, 0.4);
  }

  .grid {
    display: grid;
    gap: 1.5rem;
  }

  .grid-cols-1 {
    grid-template-columns: 1fr;
  }

  .grid-cols-2 {
    grid-template-columns: repeat(2, 1fr);
  }

  .grid-cols-3 {
    grid-template-columns: repeat(3, 1fr);
  }

  @media (max-width: 768px) {
    .grid-cols-2,
    .grid-cols-3 {
      grid-template-columns: 1fr;
    }
    
    .navbar {
      padding: 1rem;
    }
    
    .nav-links {
      flex-wrap: wrap;
      gap: 0.5rem;
    }
    
    .main-content {
      padding: 1rem;
    }
  }

  .account-item {
    padding: 1.5rem;
    border: 2px solid rgba(0, 0, 0, 0.05);
    border-radius: 12px;
    transition: all 0.3s ease;
    cursor: pointer;
    background: rgba(255, 255, 255, 0.8);
  }

  .account-item:hover {
    border-color: #667eea;
    background: rgba(255, 255, 255, 1);
    transform: translateY(-2px);
  }

  .account-item.selected {
    border-color: #667eea;
    background: rgba(102, 126, 234, 0.05);
  }

  .balance {
    font-size: 1.5rem;
    font-weight: 700;
    color: #2d3748;
    margin: 0.5rem 0;
  }

  .balance.positive {
    color: #48bb78;
  }

  .balance.negative {
    color: #f56565;
  }

  .transaction-item {
    padding: 1rem;
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .transaction-item:last-child {
    border-bottom: none;
  }

  .transaction-amount {
    font-weight: 600;
  }

  .transaction-amount.positive {
    color: #48bb78;
  }

  .transaction-amount.negative {
    color: #f56565;
  }

  .alert {
    padding: 1rem;
    border-radius: 8px;
    margin-bottom: 1rem;
    border-left: 4px solid;
  }

  .alert-success {
    background: rgba(72, 187, 120, 0.1);
    border-left-color: #48bb78;
    color: #2f855a;
  }

  .alert-error {
    background: rgba(245, 101, 101, 0.1);
    border-left-color: #f56565;
    color: #c53030;
  }

  .alert-info {
    background: rgba(102, 126, 234, 0.1);
    border-left-color: #667eea;
    color: #3182ce;
  }

  .loading {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 2rem;
  }

  .spinner {
    width: 40px;
    height: 40px;
    border: 4px solid rgba(102, 126, 234, 0.2);
    border-left-color: #667eea;
    border-radius: 50%;
    animation: spin 1s linear infinite;
  }

  @keyframes spin {
    to {
      transform: rotate(360deg);
    }
  }

  .fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .text-center {
    text-align: center;
  }

  .text-muted {
    color: #718096;
  }

  .mb-4 {
    margin-bottom: 1rem;
  }

  .mt-4 {
    margin-top: 1rem;
  }

  .error-page {
    text-align: center;
    padding: 4rem 2rem;
  }

  .error-code {
    font-size: 4rem;
    font-weight: 700;
    background: linear-gradient(45deg, #667eea, #764ba2);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: 1rem;
  }

  .error-message {
    font-size: 1.25rem;
    color: #4a5568;
    margin-bottom: 2rem;
  }
`;

function App() {
  const [selectedAccountId, setSelectedAccountId] = useState(null);

  // Inject styles into the document
  React.useEffect(() => {
    const styleElement = document.createElement('style');
    styleElement.textContent = styles;
    document.head.appendChild(styleElement);
    return () => {
      document.head.removeChild(styleElement);
    };
  }, []);

  return (
    <Router>
      <div className="app-container">
        <nav className="navbar">
          <div className="navbar-content">
            <Link to="/accounts" className="logo">
              ðŸ’° BankApp
            </Link>
            <div className="nav-links">
              <Link to="/accounts" className="nav-link">
                Accounts
              </Link>
              <Link to="/create" className="nav-link">
                Create Account
              </Link>
              <Link to="/deposit" className="nav-link">
                Deposit
              </Link>
              <Link to="/withdraw" className="nav-link">
                Withdraw
              </Link>
              <Link to="/transfer" className="nav-link">
                Transfer
              </Link>
            </div>
          </div>
        </nav>

        <main className="main-content fade-in">
          <Routes>
            {/* Redirect root to /accounts */}
            <Route path="/" element={<Navigate to="/accounts" replace />} />

            {/* List accounts */}
            <Route
              path="/accounts"
              element={
                <AccountDashboard
                  onSelectAccount={(id) => setSelectedAccountId(id)}
                  selectedAccountId={selectedAccountId}
                />
              }
            />

            {/* Account details and transaction history */}
            <Route
              path="/accounts/:accountId"
              element={<AccountDetailsWrapper />}
            />

            {/* Create a new account */}
            <Route path="/create" element={<CreateAccountForm />} />

            {/* Deposit money */}
            <Route path="/deposit" element={<DepositForm />} />

            {/* Withdraw money */}
            <Route path="/withdraw" element={<WithdrawForm />} />

            {/* Transfer money */}
            <Route path="/transfer" element={<TransferForm />} />

            {/* Fallback route */}
            <Route 
              path="*" 
              element={
                <div className="error-page">
                  <div className="error-code">404</div>
                  <div className="error-message">Page Not Found</div>
                  <Link to="/accounts" className="btn btn-primary">
                    Go to Accounts
                  </Link>
                </div>
              } 
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

// Wrapper component to get accountId param from URL
function AccountDetailsWrapper() {
  const { accountId } = useParams();
  return <AccountDetails accountId={parseInt(accountId)} />;
}

export default App;
