import React, { useEffect, useState } from 'react';
import { getTransactionHistory } from '../utils/api';

function TransactionHistory({ accountId }) {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!accountId) return;

    setLoading(true);
    getTransactionHistory(accountId)
      .then(response => {
        setTransactions(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch transaction history');
        setLoading(false);
      });
  }, [accountId]);

  if (!accountId) return <p>Please select an account to view transactions.</p>;
  if (loading) return <p>Loading transactions...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Transaction History</h2>
      {transactions.length === 0 ? (
        <p>No transactions found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Type</th>
              <th>Amount</th>
              <th>Description</th>
              <th>Recipient Account</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(tx => (
              <tr key={tx.transactionId}>
                <td>{new Date(tx.transactionDate).toLocaleString()}</td>
                <td>{tx.transactionType}</td>
                <td>{tx.amount}</td>
                <td>{tx.description || '-'}</td>
                <td>{tx.recipientAccountId || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TransactionHistory;
